$(function() {
	
	$("nav").append('<a target="_blank" class="nav" href="http://rowrz.com/themes">theme</a>');
	
	$("body").prepend('<a id="install-btn" href="https://tumblr.com/theme/38849" title="Install This Theme" target="_blank" style="opacity: 1;"><span id="install-btn-txt">Install Theme</span></a>');
	
	setTimeout(function() {
		$('#install-btn').animate({top: "3px"});
	}, 1000);
	
	var pSets = 'iframe.photoset';
	
	$.fn.photosetResize = function() {
	    $(this).each(function(){
	        var newSize = $('.posts').width();
	        var newSrc = $(this).attr('src').replace( '500' , newSize);
	        $(this).attr('src', newSrc).width(newSize);
	        var high = $(this).css('height');
	        var calculate = parseInt(high, 10) * newSize / 500;
	        $(this).css('height', calculate);
	    });
	    return this;
	};
	
	var pContainer = $('#posts');
	
	$(pSets).photosetResize();
	
    pContainer.imagesLoaded(function() {
        pContainer.masonry({
            itemSelector: '#entry',
            isResizable: true
        })
        
    });
    
    if (defensiveInfiniteScroll) {
        pContainer.infinitescroll({
                navSelector: '#page-nav',
                nextSelector: '#page-nav a',
                itemSelector: '#entry',
                loadingImg: '',
                loadingText: '',
                bufferPx: 100,
                extraScrollPx: 200,
                loading: {
                    finishedMsg: ''
                }
            },
            function(n) {
                $(n).find(pSets).photosetResize();
                $(n).hide().imagesLoaded(function() {
                    pContainer.masonry('appended', $(n).show());
                })
            }
        )
    }
});
